import React, { useState } from "react";

import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";

import "./Expenses.css";


function Expenses(props) {
  const [filteredYear, setFilteredYear] = useState("2021")
  console.log("filteredYear: ", filteredYear);

  const expenses = props.expenses.filter(expense => {
    console.log("Expense year: ", expense.date.getFullYear())
    return expense.date.getFullYear() === Number(filteredYear);
  });

  console.log("filtered expenses: ", expenses);

  const filterChangeHandler = (event) => {
    setFilteredYear(event.target.value);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter onFilterChange={filterChangeHandler} filteredYear={filteredYear} />


      {}
    </Card>
  );
}

export default Expenses;
