import React, { useState} from 'react';
import "./ExpenseForm.css";
import NewExpense from './NewExpense';


const ExpenseForm = () => {
  const [newExpense, setNewExpense] = useState({});

  const titleHandler = (event) => {
    const title = event.target.value;
    console.log("title: ", title);
    setNewExpense(...newExpense, {title: title})
    console.log("newExpense: ", NewExpense)
  }

  const amountHandler = (event) => {
    console.log("amount: ", event.target.value);
  }

  const dateHandler = (event) => {
    console.log("date: ", event.target.value);
  }


  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleHandler} placeholder="Deck Washing" />
        </div>

        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" onChange={amountHandler} min="0.01" step="0.01" placeholder="10.00" />
        </div>

        <div className="new-expense__control">
          <label>Date</label>
          <input type="Date" onChange={dateHandler} min="2019-01-01" max="2023-01-01" />
        </div>
      </div>

      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
