import React, { useState} from 'react';
import "./ExpenseForm.css";


const ExpenseForm = () => {
  const [expense, setExpense] = useState({title: null, amount: null, date: null});

  const titleHandler = (event) => {
    const title = event.target.value;
    console.log("title: ", title);
    setExpense({...expense, title: title})
    console.log("expense: ", expense);
  }

  const amountHandler = (event) => {
    const amount = event.target.amount
    console.log("amount: ", amount);
    setExpense({...expense, amount: amount});
    console.log()
  }

  const dateHandler = (event) => {
    console.log("date: ", event.target.value);
  }


  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleHandler} placeholder="Deck Washing" />
        </div>

        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" onChange={amountHandler} min="0.01" step="0.01" placeholder="10.00" />
        </div>

        <div className="new-expense__control">
          <label>Date</label>
          <input type="Date" onChange={dateHandler} min="2019-01-01" max="2023-01-01" />
        </div>
      </div>

      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
